function locktime(inicio, fim, locked, open) {
    let hora = new Date().toLocaleTimeString('pt-br', {timeStyle: 'short'})
    if(!inicio) inicio = hora
    if(!fim) fim = hora
    if(!locked) locked = 'Não é hora para isto'
    if(!open) open = 'Dentro Do Horário'
    if(hora >= inicio && hora <= fim) {
    return { 'text': locked, 'isLocked': true }
    } else { return { 'text': open, 'isLocked': false } }
}

module.exports = locktime