function timestamp() {
  
    return {

      add: function(tempo, dias) {
        if(!tempo) tempo = 'Dia'
        if(!dias) dias = 1
      
        var tempos = {
          "Segundo": 1000,
          "Minuto": 60000,
          "Hora": 3600000,
          "Dia": 86400000,
          "Semana": 604800016,
          "Mês": 2629800000,
          "Ano": 31557600000,
      }
            let calc = tempos[tempo]
            let calc2 = calc * dias
            let calc3 = Date.now() + calc2
            let result = calc3/1000
            if (isNaN(result)) {
            return 'Insira: Segundo, Minuto, Hora, Dia, Semana, Mês Ou Ano!\n Exemplo: console.log(unde.timestamp().add("Dia", 2)';
            }
           return parseInt(result);
      },
  
      sub: function(tempo, dias) {
        if(!tempo) tempo = 'Dia'
        if(!dias) dias = 1
      
        var tempos = {
            "Segundo": 1000,
            "Minuto": 60000,
            "Hora": 3600000,
            "Dia": 86400000,
            "Semana": 604800016,
            "Mês": 2629800000,
            "Ano": 31557600000,
        }
            let calc = tempos[tempo]
            let calc2 = calc * dias
            let calc3 = Date.now() - calc2
            let result = calc3/1000
            if (isNaN(result)) {
            return 'Insira: Segundo, Minuto, Hora, Dia, Semana, Mês Ou Ano!';
            }
           return parseInt(result);
      },

      now: function() {
      
           let result = Date.now()/1000
           return parseInt(result);
      },
    
    } 
  }

  module.exports = timestamp